document.addEventListener('DOMContentLoaded', () => {
  const searchInput = document.querySelector('.search-input');
  const searchSuggestions = document.querySelector('.suggestions-search');

  // Initially hide the suggestions element (optional)
  searchSuggestions?.classList?.add('hidden');

  if (searchInput && searchSuggestions) {
    searchInput.addEventListener('input', (e) => {
      const userInput = e.target.value.trim();

      if (userInput.length > 0) {
        searchSuggestions.classList.remove('hidden');

        fetch('/data/foods.json')
          .then(response => response.json())
          .then(data => {
            // ... rest of your code that filters data and populates suggestions
          });
      } else {
        searchSuggestions.classList.add('hidden');
      }
    });
  } else {
    console.error("Search suggestions element not found. Check selector.");
  }
});
