import React, { useState, useEffect, useRef } from 'react';

function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const searchInputRef = useRef(null); // Create a ref

  // ... rest of your component logic

  useEffect(() => {
    // ... your data fetching logic
  }, []);

  const handleChange = (event) => {
    setSearchTerm(event.target.value.toLowerCase());
  };

  const filteredResults = searchResults.filter((food) =>
    food.name.toLowerCase().includes(searchTerm)
  );

  return (
    <form>
      <div className="search-form">
        <p>You can discover more recipes and dishes.</p>

        <div className="searching">
          <span className="search-icon material-symbols-outlined">search</span>
          <input
            className="search-input"
            type="search"
            placeholder="Search"
            value={searchTerm}
            onChange={handleChange}
            ref={searchInputRef} // Assign the ref to the input element
          />
        </div>

        <ul className="suggestions-search">
          {/* ... your results rendering logic */}
        </ul>
      </div>
    </form>
  );
}
