var counter = 1;

function Slide() {
  const intervalId = setInterval(() => {
    const radioElement = document.getElementById('radio' + counter);
    if (radioElement) { 
      radioElement.checked = true;
    }
    counter++;
    if (counter > 5) {
      counter = 1;
    }
  }, 2000);

  return () => clearInterval(intervalId);
}

export default Slide;
