import React, { useState, useEffect } from 'react';

export async function fetchFoodData() {
  try {
    const response = await fetch('./data/foods.json'); // Adjust path if needed
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching data:', error);
    return []; 
  }
}

function Search() {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const data = await fetchFoodData();
      setSearchResults(data);
    };

    fetchData();
  }, []); 
  
  const handleChange = (event) => {
    setSearchTerm(event.target.value.toLowerCase()); // Lowercase for case-insensitive search
  };

  const filteredResults = searchResults.filter((food) =>
    food.name.toLowerCase().includes(searchTerm)
  );

  return (
    <form>
      <div className="search-form">
        <p>You can discover more recipes and dishes.</p>

        <div className="searching">
          <span className="search-icon material-symbols-outlined">search</span>
          <input
            className="search-input"
            type="search"
            placeholder="Search"
            value={searchTerm}
            onChange={handleChange}
          />
        </div>

        <ul className="suggestions-search">
          {filteredResults.length > 0 ? (
            filteredResults.map((food) => (
              <li key={food.id}>
                {food.name}
              </li>
            ))
          ) : (
            <li>No results found.</li>
          )}
        </ul>
      </div>
    </form>
  );
}

export default Search;
