import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import slideshowFirstImage from '../img/1.webp';
import slideshowSecondImage from '../img/2.webp';
import slideshowThirdImage from '../img/3.webp';
import slideshowFourthImage from '../img/4.webp';
import slideshowFifthImage from '../img/5.webp';

function Slideshow() {
  const [selectedRadio, setSelectedRadio] = useState("radio1");

  const changeSelection = (radioId) => {
    setSelectedRadio(radioId);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      changeSelection((prev) => {
        let nextNum = parseInt(prev.replace("radio", "")) + 1;
        if (nextNum > 5) nextNum = 1;
        return "radio" + nextNum;
      });
    }, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div>
      <Helmet>
        <style>{`
          .slideshow {
            padding: 6rem;
            height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
          }
          .photo-slider {
            width: 1000px;
            height: 600px;
            border-radius: 20px;
            overflow: hidden;
            position: relative; /* Added to contain absolute positioned elements */
          }
          .photo-slides {
            width: 500%;
            height: 600px;
            display: flex;
            transition: transform 1s ease; /* Smooth transition added */
          }
          .photo-slides input {
            display: none;
          }
          .slide {
            width: 20%;
            transition: transform 1s ease; /* Smooth transition added */
          }
          .slide img {
            width: 100%;
            height: 100%;
            object-fit: cover; /* Ensure images fill their containers */
          }
          .navigation-manual {
            position: absolute;
            width: 1000px;
            margin-top: -40px;
            display: flex;
            justify-content: center;
            z-index: 1; /* Ensure buttons are above slides */
          }
          .manual-btn {
            border: 2px solid #258d2a;
            background: #258d2a;
            padding: 10px;
            border-radius: 10px;
            cursor: pointer;
            transition: background-color 0.3s ease; /* Faster transition */
          }
          .manual-btn:not(:last-child) {
            margin-right: 20px; /* Reduced margin */
          }
          .manual-btn:hover {
            background: #89fc0e;
          }
          .navigation-auto {
            position: absolute;
            display: flex;
            width: 100%;
            justify-content: center;
            bottom: 20px;
            z-index: 1; /* Ensure buttons are above slides */
          }
          .navigation-auto div {
            width: 10px;
            height: 10px;
            border: 2px solid #258d2a;
            background: #258d2a;
            padding: 8px;
            border-radius: 50%;
            cursor: pointer;
            transition: background-color 0.3s ease; /* Faster transition */
            margin: 0 5px;
          }
          .navigation-auto div:hover {
            background: #0e750e;
          }
          #radio1:checked ~ .navigation-auto .auto-btn1,
          #radio2:checked ~ .navigation-auto .auto-btn2,
          #radio3:checked ~ .navigation-auto .auto-btn3,
          #radio4:checked ~ .navigation-auto .auto-btn4,
          #radio5:checked ~ .navigation-auto .auto-btn5 {
            background: #0e750e;
          }
          #radio1:checked ~ .photo-slides {
            transform: translateX(0);
          }
          #radio2:checked ~ .photo-slides {
            transform: translateX(-20%);
          }
          #radio3:checked ~ .photo-slides {
            transform: translateX(-40%);
          }
          #radio4:checked ~ .photo-slides {
            transform: translateX(-60%);
          }
          #radio5:checked ~ .photo-slides {
            transform: translateX(-80%);
          }
        `}</style>
      </Helmet>
      <div className="slideshow">
        <div className="photo-slider">
          <div className="photo-slides">
            {[...Array(5)].map((_, index) => {
              const radioId = `radio${index + 1}`;
              return (
                <input
                  key={radioId}
                  type="radio"
                  name="radio-btn"
                  id={radioId}
                  checked={selectedRadio === radioId}
                  onChange={() => changeSelection(radioId)}
                  readOnly
                />
              );
            })}
            {[slideshowFirstImage, slideshowSecondImage, slideshowThirdImage, slideshowFourthImage, slideshowFifthImage].map((image, index) => (
              <div key={index} className={`slide ${index === 0 ? "first" : ""}`}>
                <img src={image} alt={`Slide ${index + 1}`} />
              </div>
            ))}
          </div>
          <div className="navigation-manual">
            {[...Array(5)].map((_, index) => {
              const radioId = `radio${index + 1}`;
              return (
                <label key={index} htmlFor={radioId} className="manual-btn" onClick={() => changeSelection(radioId)}></label>
              );
            })}
          </div>
          <div className="navigation-auto">
            {[...Array(5)].map((_, index) => (
              <label key={index} htmlFor={`radio${index + 1}`} className={`auto-btn${index + 1}`}></label>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default Slideshow;
