import React, { useState, useEffect } from 'react';
import { Helmet } from 'react-helmet';
import slideshowFirstImage from '../img/1.webp';
import slideshowSecondImage from '../img/2.webp';
import slideshowThirdImage from '../img/3.webp';
import slideshowFourthImage from '../img/4.webp';
import slideshowFifthImage from '../img/5.webp';

function Slideshow() {
  const [selectedRadio, setSelectedRadio] = useState("radio1");

  const changeSelection = (radioId) => {
    setSelectedRadio(radioId);
  };

  useEffect(() => {
    const intervalId = setInterval(() => {
      changeSelection((prev) => {
        let nextNum = parseInt(prev.replace("radio", "")) + 1;
        if (nextNum > 5) nextNum = 1;
        return "radio" + nextNum;
      });
    }, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div> 
      <Helmet>
        <style>{`
          .slideshow {
            padding: 6rem;
            height: 100vh;
            display: flex;
            justify-content: center;
            align-items: center;
          }
          .photo-slider {
            width: 1000px;
            height: 600px;
            border-radius: 20px;
            overflow: hidden;
            position: relative; /* Ensure correct positioning */
          }
          .photo-slides {
            display: flex;
            transition: transform 2s; /* Smooth transition */
            transform: translateX(calc(-${selectedRadio.replace("radio", "") - 1} * 100%)); /* Dynamic transform */
          }
          .slide {
            width: 20%; /* Set explicit width to prevent layout shift */
            flex-shrink: 0; /* Ensure no resizing */
          }
          .slide img {
            width: 100%;
            height: 600px;
            object-fit: cover; /* Maintain aspect ratio */
          }
          /* Additional styles for manual navigation buttons */
          /* ... */
        `}</style>
      </Helmet>
      <div className="slideshow">
        <div className="photo-slider">
          <div className="photo-slides">
            {[...Array(5)].map((_, index) => {
              const radioId = `radio${index + 1}`;
              return (
                <input
                  key={radioId}
                  type="radio"
                  name="radio-btn"
                  id={radioId}
                  checked={selectedRadio === radioId}
                  onChange={() => changeSelection(radioId)}
                  readOnly
                />
              );
            })}

            {[
              slideshowFirstImage,
              slideshowSecondImage,
              slideshowThirdImage,
              slideshowFourthImage,
              slideshowFifthImage,
            ].map((image, index) => (
              <div key={index} className={`slide ${selectedRadio === `radio${index + 1}` ? "first" : ""}`}>
                <img src={image} alt={`Slide ${index + 1}`} />
              </div>
            ))}
          </div>

          {/* Navigation buttons can be added similarly */}
        </div>
      </div>
    </div> 
  );
}

export default Slideshow;
