import React from 'react';
import { Helmet } from 'react-helmet';
import { Link } from 'react-router-dom';

function Bottom() {
  return (
    <>
      <Helmet>
        <style>
          {`
            .bottom-area-adjust {
              position: relative;
              display: flex;
              justify-content: center;
              align-items: center;
              background-image: linear-gradient(to right, #0d6d0d, #258d2a, #1f9e13, #3cb400, #5eb600);
              width: 100%; /* Adjusted to full width */
              min-height: 10vh; /* Minimum height to ensure visibility */
              padding: 2rem; /* Adjust padding as needed */
            }
            .area-adjust-bottom {
              position: absolute;
              bottom: 0; /* Positioned at the bottom */
              left: 0;
              width: 100%; /* Full width */
              text-align: center; /* Center content horizontally */
            }
            .more-information {
              position: absolute;
              top: 2%;
              right: 2rem; /* Adjusted for better positioning */
            }
            .important { 
              list-style-type: none;
              padding: 0;
            }
            .important a {
              font-weight: 500;
              font-family: 'Poppins', sans-serif;
              line-height: 3;
              color: white;
              text-decoration: none; /* Removed underline */
              transition: color 0.3s ease;
            }
            .important a:hover {
              color: #89fc0e; /* Hover color */
            }
            .copyright-footer {
              position: relative; /* Changed to relative */
              margin-top: 2rem; /* Adjusted margin */
              text-align: center; /* Center content horizontally */
              color: white;
            }
            .copyright-name {
              color: white;
            }
            .fa-facebook {
              color: white;
              font-size: 20px;
              margin-left: 0.5rem; /* Adjusted spacing */
            }
          `}
        </style>
        <link
          rel="preload"
          href="https://fonts.googleapis.com/css2?family=Poppins:wght@200;400;700&display=swap"
          as="style"
          onLoad="this.onload=null;this.rel='stylesheet'"
        />
      </Helmet>

      <div className="bottom-area-adjust">
        <section className="area-adjust-bottom">
          <div className="more-information">
            <ul className="important">
              <li>
                <Link to="/legal">Legal Notice</Link>
              </li>
              <li>
                <Link to="/privacy">Privacy Notice</Link>
              </li>
              <li>
                <Link to="/cookie">Cookie Notice</Link>
              </li>
            </ul>
          </div>
        </section>

        <footer className="copyright-footer">
          <div className="copyright-name">
            <p>
              Â© 2024 Podz Ni Bordskiee
              <br />
              Follow us on Facebook <i className="fa-brands fa-facebook"></i>
              <a
                href="https://www.facebook.com/bordzkie.bedz"
                style={{ textDecoration: "none", color: "white" }}
              >
                @PodzNiBordskiee
              </a>
            </p>
          </div>
        </footer>
      </div>
    </>
  );
}

export default Bottom;
