import React, { useEffect } from 'react';
import { Helmet } from 'react-helmet';
import projectLogo from '../img/logo.webp';
import { Link } from 'react-router-dom';

function Header() {
  useEffect(() => {
    const link = document.createElement('link');
    link.rel = 'stylesheet';
    link.href = '../css/responsive.css';
    link.media = 'print';
    link.onload = function() {
      link.media = 'all';
    };
    document.head.appendChild(link);

    return () => {
      document.head.removeChild(link);
    };
  }, []);

  return (
    <>
      <Helmet>
        <style>
          {`
            .header {
              position: fixed;
              top: 0;
              left: 0;
              width: 100%;
              padding: 1.5rem 10%;
              display: flex;
              justify-content: space-between;
              align-items: center;
              z-index: 100;
            }
            .header::before {
              content: '';
              position: absolute;
              top: 0;
              left: 0;
              width: 100%;
              height: 100%;
              background-color: white;
              box-shadow: 0 .5rem 1rem rgba(0, 0, 0, .2);
              z-index: -1;
            }
            .logo {
              display: flex;
              align-items: center;
            }
            .logo img {
              width: 50px;
            }
            .navbar a {
              font-family: 'Poppins', sans-serif;
              font-size: 1.15rem;
              text-decoration: none;
              font-weight: 600;
              margin-left: 2.5rem;
            }
            .home-btn { color: #0d6d0d; }
            .recipes-btn { color: #258d2a; }
            .about-us-btn { color: #1f9e13; }
            .contact-btn { color: #3cb400; }
            .navbar a:hover { color: #89fc0e; }
            .icons { display: none; }
            #check { display: none; }
            .icons {
              position: absolute;
              left: 80%;
              font-size: 2.8rem;
              cursor: pointer;
              display: none;
            }
          `}
        </style>

        <link
          rel="preload"
          href="https://fonts.googleapis.com/css2?family=Poppins:wght@200;400;700&display=swap"
          as="style"
          onLoad="this.onload=null;this.rel='stylesheet'"
        />
        <link
          rel="preload"
          href="https://fonts.googleapis.com/css2?family=Material+Symbols+Outlined:opsz,wght,FILL,GRAD@20..48,100..700,0..1,-50..200"
          as="style"
          onLoad="this.onload=null;this.rel='stylesheet'"
        />

      </Helmet>

      <header className="header">
        <Link to="/home/" className="logo">
          <img src={projectLogo} alt="logo" />
        </Link>

        <input type="checkbox" id="check" />
        <label htmlFor="check" className="icons">
          <i className="bx bx-menu" id="menu-icon"></i>
          <i className="bx bx-x" id="close-icon"></i>
        </label>

        <nav className="navbar">
          <Link to="/home/" style={{ "--i": "0" }} className="home-btn">Home</Link>
          <Link to="/home/recipes" style={{ "--i": "1" }} className="recipes-btn">Recipes</Link>
          <Link to="/home/aboutus" style={{ "--i": "2" }} className="about-us-btn">About Us</Link>
          <Link to="/home/contactus" style={{ "--i": "3" }} className="contact-btn">Contact Us</Link>
        </nav>
      </header>
    </>
  );
}

export default Header;
